import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom/cjs/react-router-dom.min';
import styles from "./Detail.module.css";
//now we can get id !!

function Detail(){
	const [loading, setLoading] = useState(true);
	const { id } = useParams();
	const [movie, setMovie] = useState([]);
	const [stars, setStars] = useState("");
	const getStars = (rating) => {
		let stars = "";
		let i = 1;
		for (i; i < rating / 2; i++)
			stars += '‚òÖ';
		i--;
		if (rating - i * 2 >= 0.5)
			stars += '‚òÜ';
	};
	const getMovie = async () => {
		const json = await (await 
		fetch(`https://yts.mx/api/v2/movie_details.json?movie_id=${id}`
		)).json();
		console.log(json.data.movie);
		setMovie(json.data.movie);
		console.log(json.data.movie.rating, stars);
		const input_stars = getStars(json.data.movie.rating);
		setStars(getStars(json.data.movie.rating));
		setLoading(false);
		};
	useEffect(() => {
		getMovie();
	}, []);
	return (
	<div>
		{loading ? (
		<h1>Detail page loading...</h1>
		) : (
		<div>
				<h1>{movie.title_long}</h1>
				<img src={movie.large_cover_image} alt={movie.title}/>
				<hr/>
				<h2>About this movie... üßê</h2>
				<p className={styles.description}>{movie.description_full}</p>

				<h3>Rating : {stars}</h3>
				<h3>Genres : </h3>
				<ul className={styles.genre}>
					{movie.genres.map((g) => (
					<li key={g}>{g}</li>
					))}
				</ul>
		</div>)
		}
	</div>
	);
}
//challenge: add loading, and show movie details by putting json as state
export default Detail;
