import { useEffect, useState } from "react";
import Movie from "../components/Movie"
import styles from "./Home.module.css";

function Home(){
	const [loading, setLoading] = useState(true);
	const [movies, setMovies] = useState([]);
	const [minScore, setMinScore] = useState(0);
	const onSubmitt = (event) => {
		event.preventDefault();
		setLoading(true);
		fetch(`https://yts.mx/api/v2/list_movies.json?minimum_rating=${minScore}&sort_by=year`)
		.then((response) => response.json())
		.then((json) => {
			setMovies(json.data.movies);
			setLoading(false);
		});
	}
	useEffect(() => {
		fetch(`https://yts.mx/api/v2/list_movies.json?minimum_rating=5.0&sort_by=year`)
		.then((response) => response.json())
		.then((json) => {
			setMovies(json.data.movies);
			setLoading(false);
		});
	}, []);
	console.log(movies);
	return (
	<div>
		{loading ? (
		<h1>Loading...</h1>
		) : (
		<div>
			<h1>üçø Welcome to the Theatre! üçø</h1>
			<hr/>
			<form onSubmit={onSubmitt}>
				<label htmlFor="minScore">Minimum Score : </label>
				<input
					id="minScore"
					type="number"
					value={minScore}
					onChange={(e) => setMinScore(e.target.value)}
					placeholder="Minimum Score"
					required
				/>
				<button type="submit">Search</button>
			</form>
			<h2>You are viewing movies that rated higher than {minScore}.</h2>
			{movies.map((movie) => (
				<Movie
				key={movie.id} //when rendering comps inside of map
				id={movie.id}
				coverImg={movie.medium_cover_image}
				title={movie.title}
				summary={movie.summary}
				genres={movie.genres}
				/>
			))}
			</div>
			)}
			</div>
	);
}

export default Home;
